use App\Domain\Services\Interfaces\INotifierManager;
use App\Infrastructure\Services\FriendNotifier;
use App\Infrastructure\Services\AuthNotifier;
use Illuminate\Support\ServiceProvider;

class AppServiceProvider extends ServiceProvider
{
    /** @var array<class-string, class-string> $bindings */
    public array $bindings = [
        IAuthService::class => AuthService::class,
        IFriendshipService::class => FriendshipService::class,
    ];

    /** @var array<class-string, array<int, string>> $whenBindings */
    public array $whenBindings = [
        AuthService::class => [
            INotifierManager::class => AuthNotifier::class,
        ],
        FriendshipService::class => [
